INSERT INTO `ad_articles` VALUES ('42', '8', 'App\\Models\\CategoryModel', '<p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">天眼投所收录的平台，专业的线下考察团队会对其进行实地认证考察。根据实地认证考察的情况结合平台线上运营数据，对平台进行安全评级。</span></span></p><p><span style=\";font-family:宋体;font-size:14px\">&nbsp;</span></p><p><span style=\"font-family:宋体;font-size:14px\">1.</span><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">平台透明度</span></span></p><p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">评估平台上标的信息真实性，透明度情况。</span></span></p><p><span style=\"font-family:宋体;font-size:14px\">2.</span><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">违约成本</span></span></p><p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">评估平台对投资人违约所承担的隐形成本。</span></span></p><p><span style=\"font-family:宋体;font-size:14px\">3.</span><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">资金充足</span></span></p><p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">评估平台承担的资金杠杆情况</span></span></p><p><span style=\"font-family:宋体;font-size:14px\">4.</span><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">运营能力</span></span></p><p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">评估平台运营的表现</span></span></p><p><span style=\"font-family:宋体;font-size:14px\">5.</span><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">债权分散度</span></span></p><p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">评估平台结款金额的分散情况</span></span></p><p><span style=\"font-family:宋体;font-size:14px\">6.</span><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">资金流动性</span></span></p><p><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">评估平台投资人的投标资金周转速度的情况</span></span></p><p><br/></p>', '2016-09-23 11:55:53', '2016-09-23 11:55:53', null);
