INSERT INTO `ad_news` VALUES ('12', '0', '10', '佳节将至，请留意中秋值班安排', '0', null, '30', '2016-09-17 20:35:22', '2016-10-22 20:48:00', null);
INSERT INTO `ad_news` VALUES ('13', '1', '12', '《全国住房公积金2015年年度报告》发布，住房公积金隐藏多少玄机？', '0', '', '13', '2016-09-17 23:16:17', '2016-09-18 16:58:53', '2016-09-18 16:58:53');
INSERT INTO `ad_news` VALUES ('14', '1', '11', '网贷行业进入拼爹时代 增信背书或将大缩水', '0', '在后监管时代，当行业趋于理性，这种过分倚重外在的背书将还是会被平台的风控能力、投资者的风险判断所代替，这只是时间问题罢了。', '33', '2016-09-17 23:11:26', '2016-10-24 09:24:03', null);
INSERT INTO `ad_news` VALUES ('18', '1', '13', '网贷ICP办证热竟是乌龙 监管要的是EDI许可证', '0', '摘要：近日，“网贷平台别错办了ICP证”的消息盛传，记者咨询后得知，互金行业应办理的是EDI许可证。', '8', '2016-09-20 09:49:54', '2016-10-20 12:37:21', null);
INSERT INTO `ad_news` VALUES ('19', '1', '12', '关于P2P监管，投资人必须站出来说道说道！', '0', '天眼投来分析P2P监管', '21', '2016-09-20 10:21:07', '2016-10-25 09:40:30', null);
INSERT INTO `ad_news` VALUES ('20', '1', '12', '天眼投新政综合评析，及中小平台、投资人的对策建议', '0', '天眼投专家分析建议', '15', '2016-09-20 10:23:24', '2016-10-24 13:33:03', null);
INSERT INTO `ad_news` VALUES ('21', '5', '12', 'P2P合规整改必看：地方监管的考量维度', '0', 'P2P合规整改必看：地方监管的考量维度', '20', '2016-09-20 10:49:45', '2016-10-26 05:46:36', null);
INSERT INTO `ad_news` VALUES ('22', '1', '12', '8个步骤天眼投教你如何排除诈骗平台', '0', '8个步骤天眼投教你如何排除诈骗平台', '19', '2016-09-20 11:04:37', '2016-10-22 07:19:23', null);
INSERT INTO `ad_news` VALUES ('23', '1', '12', '网贷后监管，天眼投5大投资准则分享给大家', '0', '网贷后监管，天眼投5大投资准则分享给大家', '26', '2016-09-20 11:14:24', '2016-10-20 03:26:43', null);
INSERT INTO `ad_news` VALUES ('24', '1', '12', '天眼投4大攻略教你挑选高收益靠谱平台', '0', '天眼投4大攻略教你挑选高收益靠谱平台', '27', '2016-09-20 11:16:45', '2016-10-23 12:54:29', null);
INSERT INTO `ad_news` VALUES ('25', '1', '11', '汽车金融成红海 P2P平台如何突破发展瓶颈？', '0', '互联网金融机构开始涌入前景广阔的汽车金融领域，包括消费金融、P2P网贷、融资租赁等在内的互金机构均选择了不同的汽车金融业务作为入口，正式介入汽车金融领域，国内汽车金融市场也慢慢形成了群雄割据的局面', '14', '2016-09-20 14:45:38', '2016-10-20 12:36:21', null);
INSERT INTO `ad_news` VALUES ('26', '0', '14', '开发进度说明', '0', null, '0', '2016-09-20 18:57:44', '2016-09-25 00:53:56', null);
INSERT INTO `ad_news` VALUES ('27', '1', '11', '后监管时代 选择“成佛”P2P平台快捷键在哪？', '0', '中秋刚刚过完，十一黄金周却已在咫尺间招手，一些人正打算利用闲暇下来的时间选择一家靠谱的P2P网贷平台好好做场投资，但又不知从何入手。实际上这不算难事，俗话说打蛇打七寸，做事抓主要环节，那么选择P2P网贷平台只要紧抓关键点，也是可以将复杂的事情省时省心快捷来办。', '16', '2016-09-21 17:17:08', '2016-10-20 12:37:01', null);
INSERT INTO `ad_news` VALUES ('28', '1', '11', 'p2p热度不减 5月突破两万亿大关', '0', '互联网金融行业渐入健康环境下发展，尽管在监管下不合规的平台\"跑路\"引起行业负面危机，但优质的互金平台们保持着强势增长，并着眼于互金行业稳定的发展，搭建行业基础建设以实际行动振兴行业', '18', '2016-09-21 20:50:45', '2016-10-23 07:45:45', null);
INSERT INTO `ad_news` VALUES ('29', '0', '14', '增加了https', '0', null, '0', '2016-09-21 22:26:06', '2016-09-25 00:52:27', '2016-09-25 00:52:27');
INSERT INTO `ad_news` VALUES ('30', '0', '10', '国庆节放假通知', '0', null, '32', '2016-09-21 23:18:49', '2016-10-22 08:47:37', null);
INSERT INTO `ad_news` VALUES ('31', '1', '4', '1111', '0', '1123', '1', '2016-09-22 20:02:57', '2016-09-22 20:05:28', '2016-09-22 20:05:28');
INSERT INTO `ad_news` VALUES ('32', '1', '11', '李东荣：金融从业者应该具备发展数字普惠金融业务的使命感', '0', '（文/张天华）9月23日消息，中国互联网金融协会会长李东荣出席2016博鳌观察金融创新峰会，会上李东荣提出：数字普惠是金融是普惠金融发展的新思路，金融从业者应该具备发展此项业务的使命感。', '16', '2016-09-23 14:53:09', '2016-10-20 12:36:11', null);
INSERT INTO `ad_news` VALUES ('33', '1', '11', '工信部：P2P平台不需申办EDI证', '0', '工信部：P2P平台不需申办EDI证', '13', '2016-09-28 15:42:56', '2016-10-22 06:47:23', null);
INSERT INTO `ad_news` VALUES ('34', '1', '11', '“北京大学互联网金融双周论坛”第十二期', '0', '主题：《互联网+民间投融资》\n时间：2016年7月16日\n地点：北京大学经济学院', '9', '2016-10-09 09:28:32', '2016-10-23 21:12:31', null);
INSERT INTO `ad_news` VALUES ('35', '1', '11', '央行开第三方支付最大罚单 罚没金额近4700万', '0', '首批27家支付机构正在期盼牌照续发，而央行对于第三方支付机构的监管整肃重拳接连袭来。就在网络支付实名制落地20多天之后，央行对银联商务和通联支付开出了罚没金额高达4680....', '3', '2016-10-12 17:22:52', '2016-10-20 03:26:13', null);
INSERT INTO `ad_news` VALUES ('36', '0', '18', '平台说明', '0', null, '0', '2016-10-17 15:51:47', null, null);
INSERT INTO `ad_news` VALUES ('37', '0', '10', '金盈所上线通知', '0', null, '11', '2016-10-17 17:32:37', '2016-10-26 11:45:41', null);
INSERT INTO `ad_news` VALUES ('38', '1', '11', '刘晓春：金融本质是共享 互金给银行很大启发', '0', '摘要：浙商银行行长刘晓春表示，金融本质是共享，互联网金融给了银行很大的启发和灵感。', '4', '2016-10-18 10:04:42', '2016-10-25 17:19:14', null);
INSERT INTO `ad_news` VALUES ('39', '1', '11', 'P2P网贷资金存管细节曝光 指引下发在即', '0', '摘要：有消息人士透露，银监会制定的《网络借贷资金存管业务指引（征求意见稿）》不日也有望正式下发。', '8', '2016-10-19 09:26:35', '2016-10-25 17:24:51', null);
INSERT INTO `ad_news` VALUES ('40', '1', '11', '最高法：审慎处理互金案件 为创新留出空间', '0', '摘要：最高人民法院党组成员、审判委员会委员孙华璞提出，审慎处理互联网金融案件，既要依法平等保护当事人的权益，也要给互联网金融的创新发展留出适度的空间。', '4', '2016-10-26 10:43:23', '2016-10-26 14:47:02', null);

